// gridview_example.dart
import 'package:xii_rpl_1/main_layout.dart';
import 'package:flutter/material.dart';

class GridProductPage extends StatelessWidget {
  const GridProductPage({super.key});

  final List<Map<String, String>> products = const [
    {
      'name': 'T-Shirt DJ Alok',
      'price': 'Rp1.000.000',
      'image':
          'data:image/jpeg;base64,',
    },
    {
      'name': 'Hoodie Free Fire',
      'price': 'Rp500.000',
      'image':
          'data:image/jpeg;base64,',
    },
    {
      'name': 'Celana Angle Biru EFEF',
      'price': 'Rp12.500',
      'image':
          'data:image/jpeg;base64,',
    },
    {
      'name': 'Sepatu Auto Booyah',
      'price': 'Rp19.000.000',
      'image':
          'https://img.lazcdn.com/g/ff/kf/S506822522a484d84bbcd0a1bf7a68521p.jpg_360x360q80.jpg_.webp',
    },
  ];

  @override
  Widget build(BuildContext context) {
    return MainLayout(
      title: 'SET Terbaik Ini Bikin FF KaMU Aut0 Booyah!!',
      body: Padding(
        padding: const EdgeInsets.all(10),
        child: GridView.builder(
          itemCount: products.length,
          gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(
            crossAxisCount: 2, // 2 kolom
            crossAxisSpacing: 10,
            mainAxisSpacing: 10,
            childAspectRatio: 0.8,
          ),
          itemBuilder: (context, index) {
            final product = products[index];
            return Container(
              decoration: BoxDecoration(
                borderRadius: BorderRadius.circular(15),
                color: Colors.grey[100],
                boxShadow: const [
                  BoxShadow(color: Colors.black12, blurRadius: 6),
                ],
              ),
              child: Stack(
                children: [
                  // Background image
                  Positioned.fill(
                    child: ClipRRect(
                      borderRadius: BorderRadius.circular(15),
                      child: Image.network(
                        product['image']!,
                        fit: BoxFit.cover,
                      ),
                    ),
                  ),
                  // Overlay
                  Container(
                    decoration: BoxDecoration(
                      borderRadius: BorderRadius.circular(15),
                      color: Colors.black.withOpacity(0.2),
                    ),
                  ),
                  // Price & Name
                  Positioned(
                    bottom: 10,
                    left: 10,
                    right: 10,
                    child: Column(
                      crossAxisAlignment: CrossAxisAlignment.start,
                      children: [
                        Text(
                          product['name']!,
                          style: const TextStyle(
                            color: Colors.white,
                            fontWeight: FontWeight.bold,
                          ),
                        ),
                        Text(
                          product['price']!,
                          style: const TextStyle(
                            color: Colors.white,
                            fontSize: 12,
                          ),
                        ),
                      ],
                    ),
                  ),
                ],
              ),
            );
          },
        ),
      ),
    );
  }
}
